import React, { useState } from 'react';
import BaseLayout from './components/BaseLayout';
import PropertyCards from './components/PropertyCards';
import AgentChat from './components/AgentChat';
import './App.css';

// Mock data for demonstration
const mockProperty = {
  id: 1,
  address: '123 Kibo Lane, Hampstead',
  postcode: 'NW3 7AB',
  property_type: 'Detached House',
  bedrooms: 4,
  bathrooms: 3,
  price: 1250000,
  square_feet: 2340,
  days_on_market: 45,
  sstc: false,
  summary: 'A stunning Victorian detached house in prime Hampstead location. Recently renovated with modern amenities while retaining period features. Large garden and off-street parking.',
  image: '/images/property-1.jpg',
};

const mockComparables = [
  {
    id: 2,
    address: '45 Oak Street, Hampstead',
    postcode: 'NW3 7CD',
    property_type: 'Detached House',
    bedrooms: 4,
    bathrooms: 2,
    price: 1180000,
    square_feet: 2100,
    days_on_market: 32,
    sstc: true,
    summary: 'Charming Victorian house with original features',
    image: '/images/property-2.jpg',
  },
  {
    id: 3,
    address: '78 Elm Avenue, Hampstead',
    postcode: 'NW3 7EF',
    property_type: 'Detached House',
    bedrooms: 5,
    bathrooms: 3,
    price: 1420000,
    square_feet: 2650,
    days_on_market: 28,
    sstc: false,
    summary: 'Spacious family home with modern extension',
    image: '/images/property-3.jpg',
  },
];

const mockChatMessages = [
  { id: 1, content: 'Welcome to the agent chat!', is_user: false, timestamp: new Date() },
  { id: 2, content: 'Thank you! Can you tell me more about the property?', is_user: true, timestamp: new Date() },
];

function App() {
  const [selectedComparables, setSelectedComparables] = useState([]);
  const [chatMessages, setChatMessages] = useState(mockChatMessages);

  const handleSelectComparable = (id) => {
    setSelectedComparables((prev) =>
      prev.includes(id) ? prev.filter((compId) => compId !== id) : [...prev, id]
    );
  };

  const handleSendMessage = (msg) => {
    setChatMessages((prev) => [
      ...prev,
      { id: Date.now(), content: msg, is_user: true, timestamp: new Date() },
    ]);
  };

  return (
    <BaseLayout user={{ id: 1, first_name: 'John' }}>
      <div className="flex w-full h-full overflow-hidden">
        <div className="flex-1 min-w-0 h-full overflow-y-auto">
          <PropertyCards
            property={mockProperty}
            comparables={mockComparables}
            onSelectComparable={handleSelectComparable}
            selectedComparables={selectedComparables}
          />
        </div>
        <div className="w-96 min-w-[320px] max-w-[480px] h-full flex flex-col overflow-y-auto">
          <AgentChat
            chatMessages={chatMessages}
            onSendMessage={handleSendMessage}
          />
        </div>
      </div>
    </BaseLayout>
  );
}

export default App;
