import React from 'react';
import Header from './Header';

const BaseLayout = ({ children, appraisal }) => {
  return (
    <div className="min-h-screen bg-[#f8fafc]">
      <Header appraisal={appraisal} />
      {/* Add padding to account for fixed header (64px) and subnav (56px) */}
      <div className="pt-[120px]">
        {children}
      </div>
    </div>
  );
};

export default BaseLayout;
