import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
// import BaseLayout from './BaseLayout';

// Configure axios to include credentials
axios.defaults.withCredentials = true;

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [appraisals, setAppraisals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      // Fetch user and appraisals data from Flask backend
      const response = await axios.get('/api/dashboard');
      setUser(response.data.user);
      setAppraisals(response.data.appraisals || []);
    } catch (err) {
      if (err.response?.status === 401) {
        // Redirect to login if not authenticated
        window.location.href = '/login';
      } else {
        setError('Failed to load dashboard data');
        console.error('Error fetching dashboard:', err);
      }
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-GB', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const getStatusColor = (status) => {
    switch (status?.toLowerCase()) {
      case 'completed':
        return 'bg-green-100 text-green-800 border-green-200';
      case 'in progress':
        return 'bg-blue-100 text-blue-800 border-blue-200';
      case 'pending':
        return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      default:
        return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
        <div className="flex items-center justify-center h-screen">
          <div className="text-center">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
            <p className="text-gray-600">Loading your dashboard...</p>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
        <div className="flex items-center justify-center h-screen">
          <div className="text-center">
            <div className="text-red-600 text-xl mb-4">⚠️</div>
            <p className="text-red-600">{error}</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100">
      {/* Custom Dashboard Header - now fixed */}
      <div className="bg-white shadow-sm border-b border-gray-200 fixed top-0 left-0 right-0 z-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center space-x-8">
              <h1 className="text-2xl font-extrabold text-black tracking-tight">Solosway</h1>
              <nav className="flex space-x-6">
                <Link to="/dashboard" className="text-black font-semibold hover:text-indigo-700 transition">Dashboard</Link>
                <Link to="/create-appraisal" className="text-black hover:text-blue-600 transition">New Appraisal</Link>
                <Link to="/reports" className="text-black hover:text-blue-600 transition">Reports</Link>
              </nav>
            </div>
            <div className="flex items-center space-x-4">
              {user?.company_name && (
                <div className="text-sm text-black font-medium mr-2 hidden sm:block">{user.company_name}</div>
              )}
              {/* Settings icon */}
              <button className="w-9 h-9 flex items-center justify-center rounded-full hover:bg-gray-100 transition" title="Settings">
                <svg viewBox="0 0 512 512" fill="currentColor" className="w-6 h-6 text-black">
                  <path d="M487.4 315.7l-42.2-24.4c2.7-15.2 2.7-31 0-46.2l42.2-24.4c15.1-8.7 21.1-27.6 13.7-43.2l-43.1-74.6c-7.5-12.9-23.5-18.1-37-12.1l-42.2 17.9c-12.1-9.4-25.1-17.1-39.1-22.8l-6.3-45.1C332.7 13.1 319.6 0 303.7 0h-95.4c-15.9 0-29 13.1-30.5 29.7l-6.3 45.1c-14 5.7-27 13.4-39.1 22.8l-42.2-17.9c-13.5-5.7-29.5-.8-37 12.1l-43.1 74.6c-7.5 12.9-3.5 30.1 13.7 43.2l42.2 24.4c-2.7 15.2-2.7 31 0 46.2l-42.2 24.4c-15.1 8.7-21.1 27.6-13.7 43.2l43.1 74.6c7.5 12.9 23.5 18.1 37 12.1l42.2-17.9c12.1 9.4 25.1 17.1 39.1 22.8l6.3 45.1c1.5 16.6 14.6 29.7 30.5 29.7h95.4c15.9 0 29-13.1 30.5-29.7l6.3-45.1c14-5.7 27-13.4 39.1-22.8l42.2 17.9c13.5 5.7 29.5.8 37-12.1l43.1-74.6c7.5-12.9 1.5-34.5-13.7-43.2zM256 336c-44.1 0-80-35.9-80-80s35.9-80 80-80 80 35.9 80 80-35.9 80-80 80z"/>
                </svg>
              </button>
              {/* Profile avatar */}
              <div className="w-10 h-10 rounded-full overflow-hidden border-2 border-indigo-200 shadow-sm flex items-center justify-center bg-gray-100">
                <img
                  src={`https://randomuser.me/api/portraits/men/${user?.id ? (user.id % 100) : 1}.jpg`}
                  alt="Profile"
                  className="w-full h-full object-cover"
                  onError={e => { e.target.onerror = null; e.target.src = 'https://randomuser.me/api/portraits/lego/1.jpg'; }}
                />
              </div>
            </div>
          </div>
        </div>
      </div>
      {/* Main Content - add padding top for fixed header */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8 pt-24">
        {/* Welcome Section */}
        <div className="mb-8">
          <h1 className="text-4xl font-bold text-black mb-2">
            Welcome, {user?.first_name || 'User'}! 👋
          </h1>
          <p className="text-black text-lg">
            Ready to create your next property valuation?
          </p>
        </div>
        {/* Quick Actions */}
        <div className="mb-8">
          <Link
            to="/create-appraisal"
            className="inline-flex items-center px-6 py-3 bg-gradient-to-r from-blue-600 to-indigo-600 text-white font-semibold rounded-xl shadow-lg hover:from-blue-700 hover:to-indigo-700 transform hover:scale-105 transition-all duration-200"
          >
            <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
            </svg>
            Start New Valuation
          </Link>
        </div>
        {/* Recent Valuations */}
        <div className="bg-white rounded-2xl shadow-lg border border-gray-200 overflow-hidden">
          <div className="px-6 py-4 border-b border-gray-200">
            <h2 className="text-2xl font-bold text-black">Recent Valuations</h2>
            <p className="text-black mt-1">Your latest property appraisals</p>
          </div>
          {appraisals.length > 0 ? (
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-4 text-left text-sm font-semibold text-gray-900">
                      Property Address
                    </th>
                    <th className="px-6 py-4 text-left text-sm font-semibold text-gray-900">
                      Date Created
                    </th>
                    <th className="px-6 py-4 text-left text-sm font-semibold text-gray-900">
                      Status
                    </th>
                    <th className="px-6 py-4 text-left text-sm font-semibold text-gray-900">
                      Actions
                    </th>
                  </tr>
                </thead>
                <tbody className="divide-y divide-gray-200">
                  {appraisals.map((appraisal) => (
                    <tr key={appraisal.id} className="hover:bg-gray-50 transition-colors">
                      <td className="px-6 py-4">
                        <div className="flex items-center">
                          <div className="w-10 h-10 bg-blue-100 rounded-lg flex items-center justify-center mr-3">
                            <svg className="w-5 h-5 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 21V5a2 2 0 00-2-2H7a2 2 0 00-2 2v16m14 0h2m-2 0h-5m-9 0H3m2 0h5M9 7h1m-1 4h1m4-4h1m-1 4h1m-5 10v-5a1 1 0 011-1h2a1 1 0 011 1v5m-4 0h4" />
                            </svg>
                          </div>
                          <div>
                            <p className="text-sm font-medium text-gray-900">{appraisal.address}</p>
                            <p className="text-sm text-gray-500">
                              {appraisal.bedrooms && `${appraisal.bedrooms} bed`}
                              {appraisal.bathrooms && ` • ${appraisal.bathrooms} bath`}
                              {appraisal.property_type && ` • ${appraisal.property_type}`}
                            </p>
                          </div>
                        </div>
                      </td>
                      <td className="px-6 py-4 text-sm text-gray-600">
                        {formatDate(appraisal.date_created)}
                      </td>
                      <td className="px-6 py-4">
                        <span className={`inline-flex items-center px-3 py-1 rounded-full text-xs font-medium border ${getStatusColor(appraisal.status)}`}>
                          {appraisal.status}
                        </span>
                      </td>
                      <td className="px-6 py-4">
                        <Link
                          to={`/appraisal/${appraisal.id}`}
                          className="inline-flex items-center px-4 py-2 bg-blue-600 text-white text-sm font-medium rounded-lg hover:bg-blue-700 transition-colors"
                        >
                          View Details
                          <svg className="w-4 h-4 ml-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                          </svg>
                        </Link>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="px-6 py-12 text-center">
              <div className="w-16 h-16 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <svg className="w-8 h-8 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 21V5a2 2 0 00-2-2H7a2 2 0 00-2 2v16m14 0h2m-2 0h-5m-9 0H3m2 0h5M9 7h1m-1 4h1m4-4h1m-1 4h1m-5 10v-5a1 1 0 011-1h2a1 1 0 011 1v5m-4 0h4" />
                </svg>
              </div>
              <h3 className="text-lg font-medium text-gray-900 mb-2">No appraisals yet</h3>
              <p className="text-gray-600 mb-6">Get started by creating your first property valuation</p>
              <Link
                to="/create-appraisal"
                className="inline-flex items-center px-6 py-3 bg-blue-600 text-white font-semibold rounded-xl hover:bg-blue-700 transition-colors"
              >
                Create Your First Appraisal
              </Link>
            </div>
          )}
        </div>
        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mt-8">
          <div className="bg-white rounded-2xl shadow-lg p-6 border border-gray-200">
            <div className="flex items-center">
              <div className="w-12 h-12 bg-green-100 rounded-lg flex items-center justify-center mr-4">
                <svg className="w-6 h-6 text-green-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 12l2 2 4-4m6 2a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
              </div>
              <div>
                <p className="text-sm font-medium text-black">Completed</p>
                <p className="text-2xl font-bold text-black">
                  {appraisals.filter(a => a.status?.toLowerCase() === 'completed').length}
                </p>
              </div>
            </div>
          </div>
          <div className="bg-white rounded-2xl shadow-lg p-6 border border-gray-200">
            <div className="flex items-center">
              <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center mr-4">
                <svg className="w-6 h-6 text-blue-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4l3 3m6-3a9 9 0 11-18 0 9 9 0 0118 0z" />
                </svg>
              </div>
              <div>
                <p className="text-sm font-medium text-black">In Progress</p>
                <p className="text-2xl font-bold text-black">
                  {appraisals.filter(a => a.status?.toLowerCase() === 'in progress').length}
                </p>
              </div>
            </div>
          </div>
          <div className="bg-white rounded-2xl shadow-lg p-6 border border-gray-200">
            <div className="flex items-center">
              <div className="w-12 h-12 bg-purple-100 rounded-lg flex items-center justify-center mr-4">
                <svg className="w-6 h-6 text-purple-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 21V5a2 2 0 00-2-2H7a2 2 0 00-2 2v16m14 0h2m-2 0h-5m-9 0H3m2 0h5M9 7h1m-1 4h1m4-4h1m-1 4h1m-5 10v-5a1 1 0 011-1h2a1 1 0 011 1v5m-4 0h4" />
                </svg>
              </div>
              <div>
                <p className="text-sm font-medium text-black">Total Properties</p>
                <p className="text-2xl font-bold text-black">{appraisals.length}</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
